import 'dart:convert';

import 'package:fake_crypto/fake_crypto.dart';
import 'package:flutter_test/flutter_test.dart';

void main() {
  test('smoke test - rsa pkcs1', () {
    String privateKey =
        '';
    String publicKey =
        '';
    // --- sha1 ---

    RsaSigner sha1RsaSigner = RsaSigner.sha1Rsa(
        '-----BEGIN RSA PRIVATE KEY-----\n$privateKey\n-----END RSA PRIVATE KEY-----');
    expect(
      base64.encode(
          sha1RsaSigner.sign('aaa=bbb&ccc=ddd&sign_type=RSA'.codeUnits)),
      equals(''),
    );

    RsaVerifier sha1RsaVerifier = RsaVerifier.sha1Rsa(
        '-----BEGIN PUBLIC KEY-----\n$publicKey\n-----END PUBLIC KEY-----');
    expect(
      sha1RsaVerifier.verify('aaa=bbb&ccc=ddd&sign_type=RSA'.codeUnits,
          sha1RsaSigner.sign('aaa=bbb&ccc=ddd&sign_type=RSA'.codeUnits)),
      equals(true),
    );

    // --- sha256 ---

    RsaSigner sha256RsaSigner = RsaSigner.sha256Rsa(
        '-----BEGIN RSA PRIVATE KEY-----\n$privateKey\n-----END RSA PRIVATE KEY-----');
    expect(
      base64.encode(sha256RsaSigner.sign('aaa=bbb&ccc=ddd&sign_type=RSA2'.codeUnits)),
      equals(''),
    );

    RsaVerifier sha256RsaVerifier = RsaVerifier.sha256Rsa(
        '-----BEGIN PUBLIC KEY-----\n$publicKey\n-----END PUBLIC KEY-----');
    expect(
      sha256RsaVerifier.verify('aaa=bbb&ccc=ddd&sign_type=RSA2'.codeUnits,
          sha256RsaSigner.sign('aaa=bbb&ccc=ddd&sign_type=RSA2'.codeUnits)),
      equals(true),
    );
  });

  test('smoke test - rsa pkcs8', () {
    String privateKey =
        '';
    String publicKey =
        '';

    // --- sha1 ---

    RsaSigner sha1RsaSigner = RsaSigner.sha1Rsa(
        '-----BEGIN PRIVATE KEY-----\n$privateKey\n-----END PRIVATE KEY-----');
    expect(
      base64.encode(
          sha1RsaSigner.sign('aaa=bbb&ccc=ddd&sign_type=RSA'.codeUnits)),
      equals(
          ''),
    );

    RsaVerifier sha1RsaVerifier = RsaVerifier.sha1Rsa(
        '-----BEGIN PUBLIC KEY-----\n$publicKey\n-----END PUBLIC KEY-----');
    expect(
      sha1RsaVerifier.verify('aaa=bbb&ccc=ddd&sign_type=RSA'.codeUnits,
          sha1RsaSigner.sign('aaa=bbb&ccc=ddd&sign_type=RSA'.codeUnits)),
      equals(true),
    );

    // --- sha256 ---

    RsaSigner sha256RsaSigner = RsaSigner.sha256Rsa(
        '-----BEGIN PRIVATE KEY-----\n$privateKey\n-----END PRIVATE KEY-----');
    expect(
      base64.encode(
          sha256RsaSigner.sign('aaa=bbb&ccc=ddd&sign_type=RSA2'.codeUnits)),
      equals(
          ''),
    );

    RsaVerifier sha256RsaVerifier = RsaVerifier.sha256Rsa(
        '-----BEGIN PUBLIC KEY-----\n$publicKey\n-----END PUBLIC KEY-----');
    expect(
      sha256RsaVerifier.verify('aaa=bbb&ccc=ddd&sign_type=RSA2'.codeUnits,
          sha256RsaSigner.sign('aaa=bbb&ccc=ddd&sign_type=RSA2'.codeUnits)),
      equals(true),
    );
  });
}
